use std::pin::Pin;
use std::marker::PhantomPinned; #[derive(Debug)]
struct Test { a: String, b: *const String, _marker: PhantomPinned,
} impl Test { fn new(txt: &str) -> Pin<Box<Self>> { let t = Test { a: String::from(txt), b: std::ptr::null(), _marker: PhantomPinned, }; let mut boxed = Box::pin(t); let self_ptr: *const String = &boxed.as_ref().a; unsafe { boxed.as_mut().get_unchecked_mut().b = self_ptr }; boxed } fn a<'a>(self: Pin<&'a Self>) -> &'a str { &self.get_ref().a } fn b<'a>(self: Pin<&'a Self>) -> &'a String { unsafe { &*(self.b) } }
} pub fn main() { let mut test1 = Test::new("test1"); let mut test2 = Test::new("test2"); println!("a: {}, b: {}",test1.as_ref().a(), test1.as_ref().b()); // std::mem::swap(test1.get_mut(), test2.get_mut()); // std::mem::swap(&mut *test1, &mut *test2); println!("a: {}, b: {}",test2.as_ref().a(), test2.as_ref().b());
} 这里使用Box::pin()把 Test 钉在了堆上。取消注释任意一行都会编译不通过，因为 Test 是!Unpin的。

