let mut sockets: HashMap<Token, TcpStream> = HashMap::new(); // handle the event
match event.token() { Token(0) => { loop { match listener.accept() { Ok((socket, _)) => { counter += 1; let token = Token(counter); // Register for readable events poll.register(&socket, token Ready::readable(), PollOpt::edge()).unwrap(); sockets.insert(token, socket); }, Err(ref e) if e.kind() == io::ErrorKind::WouldBlock => // No more connections ready to be accepted break, Err(e) => panic!("Unexpected error: {}", e) } } }, token if event.readiness().is_readable() => { // Socket associated with token is ready for reading data from it }
}

